create table "announcement" ("id" integer generated by default as identity, "context" varchar(100), "create_time" timestamp, "create_user" varchar(255), "end_time" timestamp not null, "start_time" timestamp not null, "status" boolean not null, "title" varchar(30) not null, "employee_id" varchar(255), primary key ("id"));

