create table "recommend" ("id" integer generated by default as identity, "auther" varchar(30) not null, "isbn" varchar(13) not null, "name" varchar(100) not null, "pub_house" varchar(30) not null, "publish_date" timestamp not null, "reason" varchar(255), "status" integer not null, "emp_id" varchar(255), primary key ("id"));

