create table "reservation" ("id" integer generated by default as identity, "end_date" timestamp, "start_date" timestamp, "status" integer not null, "book_id" integer, "employee_id" varchar(255), primary key ("id"));

create table "schedule" ("id" integer generated by default as identity, "created_date" timestamp, "end_time" timestamp not null, "group" varchar(255), "job_name" varchar(255), "last_execute" varchar(2), "name" varchar(255) not null, "notice_time" time not null, "start_time" timestamp not null, "status" varchar(2) not null, "type" varchar(2) not null, "create_user_id" varchar(255), primary key ("id"));

